'use strict';

const S3270 = require('..');
const fs = require('fs');

readFile("welcome.scr").then((data) => {
    const screen = new S3270.S3270Screen(data);
    console.log(screen.ascii());
    console.log(screen.printFields());
});

function readFile(filename) {
    return new Promise((resolve, reject) => {
        fs.readFile(__dirname + "/screens/" + filename, 'utf8', (err, data) => {
            if (err) {
                return reject(err);
            }

            return resolve(data);
        });
    });
}